import { render, screen } from "@testing-library/react";

import Home from "@/app/page";

describe("Home page", () => {
  it("renders the header with navigation links", () => {
    render(<Home />);

    expect(screen.getByText("Acme")).toBeInTheDocument();
    expect(screen.getByRole("link", { name: "Home" })).toBeInTheDocument();
    expect(screen.getByRole("link", { name: "About" })).toBeInTheDocument();
    expect(screen.getByRole("link", { name: "Contact" })).toBeInTheDocument();
  });

  it("renders the hero content and call-to-action", () => {
    render(<Home />);

    expect(
      screen.getByRole("heading", { name: "Build Better Products Faster" })
    ).toBeInTheDocument();
    expect(
      screen.getByText(
        /Streamline your workflow with our all-in-one platform\. Ship features,
        track progress, and collaborate with your team seamlessly\./
      )
    ).toBeInTheDocument();
    expect(screen.getByRole("button", { name: "Get Started" })).toBeInTheDocument();
  });

  it("renders the features section", () => {
    render(<Home />);

    expect(
      screen.getByRole("heading", { name: "Everything You Need" })
    ).toBeInTheDocument();
    expect(screen.getByText("Lightning Fast")).toBeInTheDocument();
    expect(screen.getByText("Secure by Default")).toBeInTheDocument();
    expect(screen.getByText("Team Collaboration")).toBeInTheDocument();
  });

  it("renders the footer with the current year", () => {
    const currentYear = new Date().getFullYear();

    render(<Home />);

    expect(
      screen.getByText(`© ${currentYear} Acme. All rights reserved.`)
    ).toBeInTheDocument();
  });
});
